        protected override void OnLostFocus(EventArgs e)
        {
            base.OnLostFocus(e);
            if (TimeSaveFiller.LoadFile().hasTimeLeft)
            {
                Activate();
                Show();
                if (!timer1.Enabled)
                    timer1.Start();
            }
        }
        protected override void OnGotFocus(EventArgs e)
        {
            base.OnGotFocus(e);
            if (TimeSaveFiller.LoadFile().hasTimeLeft && !timer1.Enabled)
            {
                timer1.Start();
            }
        }
        protected override void OnKeyDown(KeyEventArgs e)
        {
            base.OnKeyDown(e);
        }
        protected override void OnClosed(EventArgs e)
        {
            base.OnClosed(e);
            Application.Restart();
        }
